import React, { Component } from 'react'
import { Route } from 'react-router-dom'
import { createStructuredSelector } from 'reselect'
import { connect } from 'react-redux'

import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions'
import { selectCollectionFetching } from '../../redux/shop/shop.selectors'

import Collection from '../collection/Collection.component'
import CollectionsOverview from '../../components/collections-overview/CollectionsOverview.component'

import WithSpinner from '../../components/with-spinner/WithSpinner.component'

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionsPageWithSpinner = WithSpinner(Collection);


class Shop extends Component {
    componentDidMount() {

    }

    render() {
        const {match, isCollectionFetching} = this.props
        return (
            <div className='shop-page'> 
                <Route exact path={`${match.path}`} render={(props) => <CollectionsOverviewWithSpinner isLoading={isCollectionFetching} {...props}/>} />
                <Route path={`${match.path}/:collectionId`} render={(props) => <CollectionsPageWithSpinner isLoading={isCollectionFetching} {...props}/>} />
                {/* This allows to access categoryId as a parameter on the match object */} 
            </div>
        )
    };
}


const mapStateToProps = createStructuredSelector({
    isCollectionFetching: selectCollectionFetching
})

const mapDispatchToProps = dispatch => ({
    fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
})

export default connect(mapStateToProps, mapDispatchToProps)(Shop)
