import React, { Component } from 'react'
import { Route } from 'react-router-dom'
import { createStructuredSelector } from 'reselect'
import { connect } from 'react-redux'

import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions'


import CollectionPageContainer from '../collection/Collection.container'
import CollectionsOverviewContainer from '../../components/collections-overview/CollectionsOverview.container'


class Shop extends Component {


    componentDidMount() {
        const {fetchCollectionsStartAsync} = this.props
        fetchCollectionsStartAsync()
    }

    render() {
        const {match, isCollectionsLoaded } = this.props
        return (
            <div className='shop-page'> 
                <Route exact path={`${match.path}`} component={CollectionsOverviewContainer} />
                <Route path={`${match.path}/:collectionId`} render={(props) => <CollectionsPageWithSpinner isLoading={! isCollectionsLoaded } {...props}/>} />
                {/* This allows to access categoryId as a parameter on the match object */} 
            </div>
        )
    };
}


const mapStateToProps = createStructuredSelector({
    isCollectionsLoaded: selectIsCollectionLoading
})

const mapDispatchToProps = dispatch => ({
    fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
})

export default connect(mapStateToProps, mapDispatchToProps)(Shop)
